const response = $input.item.json;
const content = response.candidates?.[0]?.content?.parts?.[0]?.text || '';
const today = new Date();
const slug = `odoo-${today.getFullYear()}-${String(today.getMonth() + 1).padStart(2, '0')}-${String(today.getDate()).padStart(2, '0')}`;

return {
  json: {
    slug,
    filename: `${slug}.md`,
    content: content,
    path: `src/content/blog/${slug}.md`
  }
};
